mapper.update:
 
	
		 update tbl_board
		 	set board_viewcnt = board_viewcnt + 1 
		 	where board_no = #{board_no}


update tbl_board 
	set
	 board_title = #{board_title},
	 board_content = #{board_content}
	 
where board_no = #{board_no} and member_nick = #{member_nick}

mapper.select:
select
	 board_no, board_title, board_content, member_nick, board_viewcnt 
	from (
		select 
				board_no, board_title, board_content, member_nick, board_viewcnt,
				row_number() over(order by board_no desc) as rNum
	    from tbl_board
		) as board
	where rNum between #{rowStart}  and #{rowEnd}
	order by board_no desc

select
	 board_no, board_title, board_content, member_nick, board_viewcnt, board_reg
	from (
		select 
				board_no, board_title, board_content, member_nick, board_viewcnt ,board_reg,
				row_number() over(order by board_no desc) as rNum
	    from tbl_board
	     
		) as board
	where rNum between #{rowStart}  and #{rowEnd}
	order by board_no desc
